func Test_sumOfTwo(t *testing.T) {
	a := []int{1, 2, 3}
	b := []int{10, 20, 30, 40}
	v := 42
	ans := true
	if got := v1(a, b, v); got != ans {
		t.Errorf("got %v, need %v \n", got, ans)
	}
	if got := v2(a, b, v); got != ans {
		t.Errorf("got %v, need %v \n", got, ans)
	}
}
